staticstructsamldb_ctx*samldb_ctx_init(structldb_module*module,
structldb_request*req)
{
structldb_context*ldb;
structsamldb_ctx*ac;

ldb=ldb_module_get_ctx(module);

ac=talloc_zero(req,structsamldb_ctx);
if(ac==NULL){
ldb_oom(ldb);
returnNULL;
}

ac->module=module;
ac->req=req;

returnac;
}

staticintsamldb_add_step(structsamldb_ctx*ac,samldb_step_fn_tfn)
{
structsamldb_step*step,*stepper;

step=talloc_zero(ac,structsamldb_step);
if(step==NULL){
returnldb_oom(ldb_module_get_ctx(ac->module));
}

step->fn=fn;

if(ac->steps==NULL){
ac->steps=step;
ac->curstep=step;
}else{
if(ac->curstep==NULL)
returnldb_operr(ldb_module_get_ctx(ac->module));
for(stepper=ac->curstep;stepper->next!=NULL;
stepper=stepper->next);
stepper->next=step;
}

returnLDB_SUCCESS;
}

staticintsamldb_first_step(structsamldb_ctx*ac)
{
if(ac->steps==NULL){
returnldb_operr(ldb_module_get_ctx(ac->module));
}

ac->curstep=ac->steps;
returnac->curstep->fn(ac);
}

staticintsamldb_next_step(structsamldb_ctx*ac)
{
if(ac->curstep->next){
ac->curstep=ac->curstep->next;
returnac->curstep->fn(ac);
}

/*Weexitthesamldbmodulehere.Ifsomeonesetan"ares"toforward
*controlsandresponsebacktothecaller,usethem.*/
if(ac->ares){
returnldb_module_done(ac->req,ac->ares->controls,
ac->ares->response,LDB_SUCCESS);
}else{
returnldb_module_done(ac->req,NULL,NULL,LDB_SUCCESS);
}
}


