returnldb_module_send_referral(ac->req,ares->referral);
}

if(ares->error!=LDB_SUCCESS){
returnldb_module_done(ac->req,ares->controls,
ares->response,ares->error);
}
if(ares->type!=LDB_REPLY_DONE){
ldb_asprintf_errstring(ldb,"InvalidLDBreplytype%d",ares->type);
returnldb_module_done(ac->req,NULL,NULL,
LDB_ERR_OPERATIONS_ERROR);
}

/*Thecallermaywishtogetcontrolsbackfromtheadd*/
ac->ares=talloc_steal(ac,ares);

ret=samldb_next_step(ac);
if(ret!=LDB_SUCCESS){
returnldb_module_done(ac->req,NULL,NULL,ret);
}
returnret;
}

