staticintsamldb_check_user_account_control_rules(structsamldb_ctx*ac,
structdom_sid*sid,
uint32_tuser_account_control,
uint32_tuser_account_control_old);

/*
*"Objectclass"trigger(MS-SAMR3.1.1.8.1)
*
*Hastobeinvokedon"add"and"modify"operationson"user","computer"and
*"group"objects.
*ac->msgcontainsthe"add"/"modify"message
*ac->typecontainstheobjecttype(mainobjectclass)
*/
staticintsamldb_objectclass_trigger(structsamldb_ctx*ac)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
void*skip_allocate_sids=ldb_get_opaque(ldb,
"skip_allocate_sids");
structldb_message_element*el,*el2;
structdom_sid*sid;
intret;

/*makesurethat"sAMAccountType"isnotspecified*/
el=ldb_msg_find_element(ac->msg,"sAMAccountType");
if(el!=NULL){
ldb_set_errstring(ldb,
"samldb:sAMAccountTypemustnotbespecified!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*Step1:objectSidassignment*/

/*Don'tallowtheobjectSidtobechanged.ButbesidetheRELAX
*controlwehavealsotoguaranteethatitcanalwaysbesetwith
*SYSTEMpermissions.Thisisneededforthe"samba3sam"backend.*/
sid=samdb_result_dom_sid(ac,ac->msg,"objectSid");
if((sid!=NULL)&&(!dsdb_module_am_system(ac->module))&&
(ldb_request_get_control(ac->req,LDB_CONTROL_RELAX_OID)==NULL)){
ldb_set_errstring(ldb,
"samldb:objectSidmustnotbespecified!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*butgenerateanewSIDwhenwedohaveanaddoperations*/
if((sid==NULL)&&(ac->req->operation==LDB_ADD)&&!skip_allocate_sids){
ret=samldb_add_step(ac,samldb_allocate_sid);
if(ret!=LDB_SUCCESS)returnret;
}

switch(ac->type){
caseSAMLDB_TYPE_USER:{
booluac_generated=false,uac_add_flags=false;

/*Step1.2:Defaultvalues*/
ret=dsdb_user_obj_set_defaults(ldb,ac->msg);
if(ret!=LDB_SUCCESS)returnret;

/*Onaddoperationswemightneedtogeneratea
*"userAccountControl"(ifitisn'tspecified).*/
el=ldb_msg_find_element(ac->msg,"userAccountControl");
if((el==NULL)&&(ac->req->operation==LDB_ADD)){
ret=samdb_msg_set_uint(ldb,ac->msg,ac->msg,
"userAccountControl",
UF_NORMAL_ACCOUNT);
if(ret!=LDB_SUCCESS){
returnret;
}
uac_generated=true;
uac_add_flags=true;
}

el=ldb_msg_find_element(ac->msg,"userAccountControl");
if(el!=NULL){
uint32_tuser_account_control;
/*Step1.3:"userAccountControl"->"sAMAccountType"mapping*/
user_account_control=ldb_msg_find_attr_as_uint(ac->msg,
"userAccountControl",
0);
/*
*"userAccountControl"=0ormissingoneof
*thetypesmeans"UF_NORMAL_ACCOUNT".See
*MS-SAMR3.1.1.8.10point8
*/
if((user_account_control&UF_ACCOUNT_TYPE_MASK)==0){
user_account_control=UF_NORMAL_ACCOUNT|user_account_control;
uac_generated=true;
}

/*
*AsperMS-SAMR3.1.1.8.10theseflagshavenottobeset
*/
if((user_account_control&UF_LOCKOUT)!=0){
user_account_control&=~UF_LOCKOUT;
uac_generated=true;
}
if((user_account_control&UF_PASSWORD_EXPIRED)!=0){
user_account_control&=~UF_PASSWORD_EXPIRED;
uac_generated=true;
}

ret=samldb_check_user_account_control_rules(ac,NULL,
user_account_control,0);
if(ret!=LDB_SUCCESS){
returnret;
}

/*Workstationand(read-only)DCobjectsdoneedobjectclass"computer"*/
if((samdb_find_attribute(ldb,ac->msg,
"objectclass","computer")==NULL)&&
(user_account_control&
(UF_SERVER_TRUST_ACCOUNT|UF_WORKSTATION_TRUST_ACCOUNT))){
ldb_set_errstring(ldb,
"samldb:Requestedaccounttypedoesneedobjectclass'computer'!");
returnLDB_ERR_OBJECT_CLASS_VIOLATION;
}

/*add"sAMAccountType"attribute*/
ret=dsdb_user_obj_set_account_type(ldb,ac->msg,user_account_control,NULL);
if(ret!=LDB_SUCCESS){
returnret;
}

/*"isCriticalSystemObject"mightbeset*/
if(user_account_control&
(UF_SERVER_TRUST_ACCOUNT|UF_PARTIAL_SECRETS_ACCOUNT)){
ret=ldb_msg_add_string(ac->msg,"isCriticalSystemObject",
"TRUE");
if(ret!=LDB_SUCCESS){
returnret;
}
el2=ldb_msg_find_element(ac->msg,
"isCriticalSystemObject");
el2->flags=LDB_FLAG_MOD_REPLACE;
}elseif(user_account_control&UF_WORKSTATION_TRUST_ACCOUNT){
ret=ldb_msg_add_string(ac->msg,"isCriticalSystemObject",
"FALSE");
if(ret!=LDB_SUCCESS){
returnret;
}
el2=ldb_msg_find_element(ac->msg,
"isCriticalSystemObject");
el2->flags=LDB_FLAG_MOD_REPLACE;
}

/*Step1.4:"userAccountControl"->"primaryGroupID"mapping*/
if(!ldb_msg_find_element(ac->msg,"primaryGroupID")){
uint32_trid;

ret=dsdb_user_obj_set_primary_group_id(ldb,ac->msg,user_account_control,&rid);
if(ret!=LDB_SUCCESS){
returnret;
}
/*
*OlderADdeploymentsdon'tknowaboutthe
*RODCgroup
*/
if(rid==DOMAIN_RID_READONLY_DCS){
ret=samldb_prim_group_tester(ac,rid);
if(ret!=LDB_SUCCESS){
returnret;
}
}
}

/*Step1.5:Addadditionalflagswhenneeded*/
/*Obviouslythisisdonewhenthe"userAccountControl"
*hasbeengeneratedhere(testedagainstWindows
*Server)*/
if(uac_generated){
if(uac_add_flags){
user_account_control|=UF_ACCOUNTDISABLE;
user_account_control|=UF_PASSWD_NOTREQD;
}

ret=samdb_msg_set_uint(ldb,ac->msg,ac->msg,
"userAccountControl",
user_account_control);
if(ret!=LDB_SUCCESS){
returnret;
}
}

}
break;
}

caseSAMLDB_TYPE_GROUP:{
constchar*tempstr;

/*Step2.2:Defaultvalues*/
tempstr=talloc_asprintf(ac->msg,"%d",
GTYPE_SECURITY_GLOBAL_GROUP);
if(tempstr==NULL)returnldb_operr(ldb);
ret=samdb_find_or_add_attribute(ldb,ac->msg,
"groupType",tempstr);
if(ret!=LDB_SUCCESS)returnret;

/*Step2.3:"groupType"->"sAMAccountType"*/
el=ldb_msg_find_element(ac->msg,"groupType");
if(el!=NULL){
uint32_tgroup_type,account_type;

group_type=ldb_msg_find_attr_as_uint(ac->msg,
"groupType",0);

/*Thecreationofbuiltingroupsrequiresthe
*RELAXcontrol*/
if(group_type==GTYPE_SECURITY_BUILTIN_LOCAL_GROUP){
if(ldb_request_get_control(ac->req,
LDB_CONTROL_RELAX_OID)==NULL){
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}

account_type=ds_gtype2atype(group_type);
if(account_type==0){
ldb_set_errstring(ldb,"samldb:Unrecognizedaccounttype!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
ret=samdb_msg_add_uint(ldb,ac->msg,ac->msg,
"sAMAccountType",
account_type);
if(ret!=LDB_SUCCESS){
returnret;
}
el2=ldb_msg_find_element(ac->msg,"sAMAccountType");
el2->flags=LDB_FLAG_MOD_REPLACE;
}
break;
}

default:
ldb_asprintf_errstring(ldb,
"Invalidentrytype!");
returnLDB_ERR_OPERATIONS_ERROR;
break;
}

returnLDB_SUCCESS;
}

/*
*"PrimarygroupID"trigger(MS-SAMR3.1.1.8.2)
