staticintsamldb_check_user_account_control_invariants(structsamldb_ctx*ac,
uint32_tuser_account_control)
{
inti,ret=0;
boolneed_check=false;
conststructuac_to_guid{
uint32_tuac;
boolnever;
uint32_tneeds;
uint32_tnot_with;
constchar*error_string;
}map[]={
{
.uac=UF_TEMP_DUPLICATE_ACCOUNT,
.never=true,
.error_string="UpdatingtheUF_TEMP_DUPLICATE_ACCOUNTflagisneverallowed"
},
{
.uac=UF_PARTIAL_SECRETS_ACCOUNT,
.needs=UF_WORKSTATION_TRUST_ACCOUNT,
.error_string="SettingUF_PARTIAL_SECRETS_ACCOUNTonlypermittedwithUF_WORKSTATION_TRUST_ACCOUNT"
},
{
.uac=UF_TRUSTED_FOR_DELEGATION,
.not_with=UF_PARTIAL_SECRETS_ACCOUNT,
.error_string="SettingUF_TRUSTED_FOR_DELEGATIONnotallowedwithUF_PARTIAL_SECRETS_ACCOUNT"
},
{
.uac=UF_NORMAL_ACCOUNT,
.not_with=UF_ACCOUNT_TYPE_MASK&~UF_NORMAL_ACCOUNT,
.error_string="Settingmorethanoneaccounttypenotpermitted"
},
{
.uac=UF_WORKSTATION_TRUST_ACCOUNT,
.not_with=UF_ACCOUNT_TYPE_MASK&~UF_WORKSTATION_TRUST_ACCOUNT,
.error_string="Settingmorethanoneaccounttypenotpermitted"
},
{
.uac=UF_INTERDOMAIN_TRUST_ACCOUNT,
.not_with=UF_ACCOUNT_TYPE_MASK&~UF_INTERDOMAIN_TRUST_ACCOUNT,
.error_string="Settingmorethanoneaccounttypenotpermitted"
},
{
.uac=UF_SERVER_TRUST_ACCOUNT,
.not_with=UF_ACCOUNT_TYPE_MASK&~UF_SERVER_TRUST_ACCOUNT,
.error_string="Settingmorethanoneaccounttypenotpermitted"
},
{
.uac=UF_TRUSTED_FOR_DELEGATION,
.not_with=UF_PARTIAL_SECRETS_ACCOUNT,
.error_string="SettingUF_TRUSTED_FOR_DELEGATIONnotallowedwithUF_PARTIAL_SECRETS_ACCOUNT"
}
};

for(i=0;i<ARRAY_SIZE(map);i++){
if(user_account_control&map[i].uac){
need_check=true;
break;
}
}
if(need_check==false){
returnLDB_SUCCESS;
}

for(i=0;i<ARRAY_SIZE(map);i++){
uint32_tthis_uac=user_account_control&map[i].uac;
if(this_uac!=0){
if(map[i].never){
ret=LDB_ERR_OTHER;
break;
}elseif(map[i].needs!=0){
if((map[i].needs&user_account_control)==0){
ret=LDB_ERR_OTHER;
break;
}
}elseif(map[i].not_with!=0){
if((map[i].not_with&user_account_control)!=0){
ret=LDB_ERR_OTHER;
break;
}
}
}
}
if(ret!=LDB_SUCCESS){
switch(ac->req->operation){
caseLDB_ADD:
ldb_asprintf_errstring(ldb_module_get_ctx(ac->module),
"Failedtoadd%s:%s",
ldb_dn_get_linearized(ac->msg->dn),
map[i].error_string);
break;
caseLDB_MODIFY:
ldb_asprintf_errstring(ldb_module_get_ctx(ac->module),
"Failedtomodify%s:%s",
ldb_dn_get_linearized(ac->msg->dn),
map[i].error_string);
break;
default:
returnldb_module_operr(ac->module);
}
}
returnret;
}

