/**
*ThisfunctioniscalledonLDBmodifyoperations.Itperformssomeadditions/
*replacesonthecurrentLDBmessagewhen"userAccountControl"changes.
*/
staticintsamldb_user_account_control_change(structsamldb_ctx*ac)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
uint32_told_uac;
uint32_tnew_uac;
uint32_traw_uac;
uint32_told_ufa;
uint32_tnew_ufa;
uint32_told_uac_computed;
uint32_tclear_uac;
uint32_told_atype;
uint32_tnew_atype;
uint32_told_pgrid;
uint32_tnew_pgrid;
NTTIMEold_lockoutTime;
structldb_message_element*el;
structldb_val*val;
structldb_valcomputer_val;
structldb_message*tmp_msg;
structdom_sid*sid;
intret;
structldb_result*res;
constchar*constattrs[]={
"objectClass",
"isCriticalSystemObject",
"userAccountControl",
"msDS-User-Account-Control-Computed",
"lockoutTime",
"objectSid",
NULL
};
boolis_computer=false;
boolold_is_critical=false;
boolnew_is_critical=false;

el=dsdb_get_single_valued_attr(ac->msg,"userAccountControl",
ac->req->operation);
if(el==NULL||el->num_values==0){
ldb_asprintf_errstring(ldb,
"%08X:samldb:'userAccountControl'can'tbedeleted!",
W_ERROR_V(WERR_DS_ILLEGAL_MOD_OPERATION));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*Createatemporarymessageforfetchingthe"userAccountControl"*/
tmp_msg=ldb_msg_new(ac->msg);
if(tmp_msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(tmp_msg,el,0);
if(ret!=LDB_SUCCESS){
returnret;
}
raw_uac=ldb_msg_find_attr_as_uint(tmp_msg,
"userAccountControl",
0);
talloc_free(tmp_msg);
/*
*UF_LOCKOUT,UF_PASSWD_CANT_CHANGEandUF_PASSWORD_EXPIRED
*areonlygeneratedandnotstored.Weignorethemalmost
*completely,alongwithunknownbitsandUF_SCRIPT.
*
*TheonlyexceptionisACB_AUTOLOCK,whichfeaturesin
*clear_acbwhenthebitisclearedinthismodifyoperation.
*
*MS-SAMR2.2.1.13UF_FLAGCodesstatesthatsomebitsare
*ignoredbyclientsandservers
*/
new_uac=raw_uac&UF_SETTABLE_BITS;

/*Fetchtheold"userAccountControl"and"objectClass"*/
ret=dsdb_module_search_dn(ac->module,ac,&res,ac->msg->dn,attrs,
DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}
old_uac=ldb_msg_find_attr_as_uint(res->msgs[0],"userAccountControl",0);
if(old_uac==0){
returnldb_operr(ldb);
}
old_uac_computed=ldb_msg_find_attr_as_uint(res->msgs[0],
"msDS-User-Account-Control-Computed",0);
old_lockoutTime=ldb_msg_find_attr_as_int64(res->msgs[0],
"lockoutTime",0);
old_is_critical=ldb_msg_find_attr_as_bool(res->msgs[0],
"isCriticalSystemObject",0);
/*Whenwedonothaveobjectclass"computer"wecannotswitchtoa(read-only)DC*/
el=ldb_msg_find_element(res->msgs[0],"objectClass");
if(el==NULL){
returnldb_operr(ldb);
}
computer_val=data_blob_string_const("computer");
val=ldb_msg_find_val(el,&computer_val);
if(val!=NULL){
is_computer=true;
}

old_ufa=old_uac&UF_ACCOUNT_TYPE_MASK;
old_atype=ds_uf2atype(old_ufa);
old_pgrid=ds_uf2prim_group_rid(old_uac);

new_ufa=new_uac&UF_ACCOUNT_TYPE_MASK;
if(new_ufa==0){
/*
*"userAccountControl"=0ormissingoneofthe
*typesmeans"UF_NORMAL_ACCOUNT".SeeMS-SAMR
*3.1.1.8.10point8
*/
new_ufa=UF_NORMAL_ACCOUNT;
new_uac|=new_ufa;
}
sid=samdb_result_dom_sid(res,res->msgs[0],"objectSid");
if(sid==NULL){
returnldb_module_operr(ac->module);
}

ret=samldb_check_user_account_control_rules(ac,sid,new_uac,old_uac);
if(ret!=LDB_SUCCESS){
returnret;
}

new_atype=ds_uf2atype(new_ufa);
new_pgrid=ds_uf2prim_group_rid(new_uac);

clear_uac=(old_uac|old_uac_computed)&~raw_uac;

switch(new_ufa){
caseUF_NORMAL_ACCOUNT:
new_is_critical=old_is_critical;
break;

caseUF_INTERDOMAIN_TRUST_ACCOUNT:
new_is_critical=true;
break;

caseUF_WORKSTATION_TRUST_ACCOUNT:
new_is_critical=false;
if(new_uac&UF_PARTIAL_SECRETS_ACCOUNT){
if(!is_computer){
ldb_asprintf_errstring(ldb,
"%08X:samldb:UF_PARTIAL_SECRETS_ACCOUNT"
"requiresobjectclass'computer'!",
W_ERROR_V(WERR_DS_MACHINE_ACCOUNT_CREATED_PRENT4));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
new_is_critical=true;
}
break;

caseUF_SERVER_TRUST_ACCOUNT:
if(!is_computer){
ldb_asprintf_errstring(ldb,
"%08X:samldb:UF_SERVER_TRUST_ACCOUNT"
"requiresobjectclass'computer'!",
W_ERROR_V(WERR_DS_MACHINE_ACCOUNT_CREATED_PRENT4));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
new_is_critical=true;
break;

default:
ldb_asprintf_errstring(ldb,
"%08X:samldb:invaliduserAccountControl[0x%08X]",
W_ERROR_V(WERR_INVALID_PARAMETER),raw_uac);
returnLDB_ERR_OTHER;
}

if(old_atype!=new_atype){
ret=samdb_msg_add_uint(ldb,ac->msg,ac->msg,
"sAMAccountType",new_atype);
if(ret!=LDB_SUCCESS){
returnret;
}
el=ldb_msg_find_element(ac->msg,"sAMAccountType");
el->flags=LDB_FLAG_MOD_REPLACE;
}

/*AsperMS-SAMR3.1.1.8.10theseflagshavenottobeset*/
if((clear_uac&UF_LOCKOUT)&&(old_lockoutTime!=0)){
/*"pwdLastSet"resetaspasswordexpirationhasbeenforced*/
ldb_msg_remove_attr(ac->msg,"lockoutTime");
ret=samdb_msg_add_uint64(ldb,ac->msg,ac->msg,"lockoutTime",
(NTTIME)0);
if(ret!=LDB_SUCCESS){
returnret;
}
el=ldb_msg_find_element(ac->msg,"lockoutTime");
el->flags=LDB_FLAG_MOD_REPLACE;
}

/*"isCriticalSystemObject"mightbeset/changed*/
if(old_is_critical!=new_is_critical){
ret=ldb_msg_add_string(ac->msg,"isCriticalSystemObject",
new_is_critical?"TRUE":"FALSE");
if(ret!=LDB_SUCCESS){
returnret;
}
el=ldb_msg_find_element(ac->msg,
"isCriticalSystemObject");
el->flags=LDB_FLAG_MOD_REPLACE;
}

if(!ldb_msg_find_element(ac->msg,"primaryGroupID")&&
(old_pgrid!=new_pgrid)){
/*OlderADdeploymentsdon'tknowabouttheRODCgroup*/
if(new_pgrid==DOMAIN_RID_READONLY_DCS){
ret=samldb_prim_group_tester(ac,new_pgrid);
if(ret!=LDB_SUCCESS){
returnret;
}
}

ret=samdb_msg_add_uint(ldb,ac->msg,ac->msg,
"primaryGroupID",new_pgrid);
if(ret!=LDB_SUCCESS){
returnret;
}
el=ldb_msg_find_element(ac->msg,
"primaryGroupID");
el->flags=LDB_FLAG_MOD_REPLACE;
}

/*Propagateeventual"userAccountControl"attributechanges*/
if(old_uac!=new_uac){
char*tempstr=talloc_asprintf(ac->msg,"%d",
new_uac);
if(tempstr==NULL){
returnldb_module_oom(ac->module);
}

/*Overwrite"userAccountControl"correctly*/
el=dsdb_get_single_valued_attr(ac->msg,"userAccountControl",
ac->req->operation);
el->values[0].data=(uint8_t*)tempstr;
el->values[0].length=strlen(tempstr);
}else{
ldb_msg_remove_attr(ac->msg,"userAccountControl");
}

returnLDB_SUCCESS;
}

