staticintsamldb_group_type_change(structsamldb_ctx*ac)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
uint32_tgroup_type,old_group_type,account_type;
structldb_message_element*el;
structldb_message*tmp_msg;
intret;
structldb_result*res;
constchar*constattrs[]={"groupType",NULL};

el=dsdb_get_single_valued_attr(ac->msg,"groupType",
ac->req->operation);
if(el==NULL){
/*wearenotaffected*/
returnLDB_SUCCESS;
}

/*Createatemporarymessageforfetchingthe"groupType"*/
tmp_msg=ldb_msg_new(ac->msg);
if(tmp_msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(tmp_msg,el,0);
if(ret!=LDB_SUCCESS){
returnret;
}
group_type=ldb_msg_find_attr_as_uint(tmp_msg,"groupType",0);
talloc_free(tmp_msg);

ret=dsdb_module_search_dn(ac->module,ac,&res,ac->msg->dn,attrs,
DSDB_FLAG_NEXT_MODULE|
DSDB_SEARCH_SHOW_DELETED,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}
old_group_type=ldb_msg_find_attr_as_uint(res->msgs[0],"groupType",0);
if(old_group_type==0){
returnldb_operr(ldb);
}

/*Grouptypeswitchingisn'tsoeasyasitseems:Wecanonly
*changeinthisdirections:global<->universal<->local
*Oneachstepalsothegrouptypeitself
*(security/distribution)isvariable.*/

if(ldb_request_get_control(ac->req,LDB_CONTROL_PROVISION_OID)==NULL){
switch(group_type){
caseGTYPE_SECURITY_GLOBAL_GROUP:
caseGTYPE_DISTRIBUTION_GLOBAL_GROUP:
/*changeto"universal"allowed*/
if((old_group_type==GTYPE_SECURITY_DOMAIN_LOCAL_GROUP)||
(old_group_type==GTYPE_DISTRIBUTION_DOMAIN_LOCAL_GROUP)){
ldb_set_errstring(ldb,
"samldb:Changefromsecurity/distributionlocalgroupforbidden!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
break;

caseGTYPE_SECURITY_UNIVERSAL_GROUP:
caseGTYPE_DISTRIBUTION_UNIVERSAL_GROUP:
/*eachchangeallowed*/
break;
caseGTYPE_SECURITY_DOMAIN_LOCAL_GROUP:
caseGTYPE_DISTRIBUTION_DOMAIN_LOCAL_GROUP:
/*changeto"universal"allowed*/
if((old_group_type==GTYPE_SECURITY_GLOBAL_GROUP)||
(old_group_type==GTYPE_DISTRIBUTION_GLOBAL_GROUP)){
ldb_set_errstring(ldb,
"samldb:Changefromsecurity/distributionglobalgroupforbidden!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
break;

caseGTYPE_SECURITY_BUILTIN_LOCAL_GROUP:
default:
/*wedon'tallowthis"groupType"values*/
returnLDB_ERR_UNWILLING_TO_PERFORM;
break;
}
}

account_type=ds_gtype2atype(group_type);
if(account_type==0){
ldb_set_errstring(ldb,"samldb:Unrecognizedaccounttype!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
ret=samdb_msg_add_uint(ldb,ac->msg,ac->msg,"sAMAccountType",
account_type);
if(ret!=LDB_SUCCESS){
returnret;
}
el=ldb_msg_find_element(ac->msg,"sAMAccountType");
el->flags=LDB_FLAG_MOD_REPLACE;

returnLDB_SUCCESS;
}

staticintsamldb_sam_accountname_check(structsamldb_ctx*ac)
