structldb_context*ldb=ldb_module_get_ctx(ac->module);
constchar*constno_attrs[]={NULL};
structldb_result*res;
constchar*sam_accountname,*enc_str;
structldb_message_element*el;
structldb_message*tmp_msg;
intret;

el=dsdb_get_single_valued_attr(ac->msg,"sAMAccountName",
ac->req->operation);
if(el==NULL){
/*wearenotaffected*/
returnLDB_SUCCESS;
}

/*Createatemporarymessageforfetchingthe"sAMAccountName"*/
tmp_msg=ldb_msg_new(ac->msg);
if(tmp_msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(tmp_msg,el,0);
if(ret!=LDB_SUCCESS){
returnret;
}

/*Wemustnotstealtheoriginalstring,itbelongstothecaller!*/
sam_accountname=talloc_strdup(ac,
ldb_msg_find_attr_as_string(tmp_msg,"sAMAccountName",NULL));
talloc_free(tmp_msg);

if(sam_accountname==NULL){
/*The"sAMAccountName"cannotbenothing*/
ldb_set_errstring(ldb,
"samldb:Emptyaccountnamesaren'tallowed!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

enc_str=ldb_binary_encode_string(ac,sam_accountname);
if(enc_str==NULL){
returnldb_module_oom(ac->module);
}

/*Makesurethata"sAMAccountName"isonlyusedonce*/

ret=dsdb_module_search(ac->module,ac,&res,
ldb_get_default_basedn(ldb),
LDB_SCOPE_SUBTREE,no_attrs,
DSDB_FLAG_NEXT_MODULE,ac->req,
"(sAMAccountName=%s)",enc_str);
if(ret!=LDB_SUCCESS){
returnret;
}
if(res->count>1){
returnldb_operr(ldb);
}elseif(res->count==1){
if(ldb_dn_compare(res->msgs[0]->dn,ac->msg->dn)!=0){
ldb_asprintf_errstring(ldb,
"samldb:Accountname(sAMAccountName)'%s'alreadyinuse!",
sam_accountname);
returnLDB_ERR_ENTRY_ALREADY_EXISTS;
}
}
talloc_free(res);

returnLDB_SUCCESS;
}

staticintsamldb_member_check(structsamldb_ctx*ac)
{
constchar*constattrs[]={"objectSid",NULL};
structldb_context*ldb=ldb_module_get_ctx(ac->module);
structldb_message_element*el;
structldb_dn*member_dn;
structdom_sid*sid;
structldb_result*res;
structdom_sid*group_sid;
unsignedinti,j;
intret;

/*Fetchinformationfromtheexistingobject*/

ret=dsdb_module_search(ac->module,ac,&res,ac->msg->dn,LDB_SCOPE_BASE,attrs,
DSDB_FLAG_NEXT_MODULE|DSDB_SEARCH_SHOW_DELETED,ac->req,NULL);
if(ret!=LDB_SUCCESS){
returnret;
}
if(res->count!=1){
returnldb_operr(ldb);
}

group_sid=samdb_result_dom_sid(res,res->msgs[0],"objectSid");
if(group_sid==NULL){
returnldb_operr(ldb);
}

/*We'vetowalkoverallmodificationentriesandconsiderthe"member"
*ones.*/
for(i=0;i<ac->msg->num_elements;i++){
