continue;
}

el=&ac->msg->elements[i];
for(j=0;j<el->num_values;j++){
structldb_result*group_res;
constchar*group_attrs[]={"primaryGroupID",NULL};
uint32_tprim_group_rid;

if(LDB_FLAG_MOD_TYPE(el->flags)==LDB_FLAG_MOD_DELETE){
/*Deleteswillbehandledin
*repl_meta_data,anddeletesnot
*matchingamemberwillreturn
*LDB_ERR_UNWILLING_TO_PERFORM
*there*/
continue;
}

member_dn=ldb_dn_from_ldb_val(ac,ldb,
&el->values[j]);
if(!ldb_dn_validate(member_dn)){
returnldb_operr(ldb);
}

/*Deniestoadd"member"stogroupswhichareprimary
*onesforthem-inthiscasereturn
*ERR_ENTRY_ALREADY_EXISTS.*/

ret=dsdb_module_search_dn(ac->module,ac,&group_res,
member_dn,group_attrs,
DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret==LDB_ERR_NO_SUCH_OBJECT){
/*memberDNdoesn'texistyet*/
continue;
}
if(ret!=LDB_SUCCESS){
returnret;
}
prim_group_rid=ldb_msg_find_attr_as_uint(group_res->msgs[0],"primaryGroupID",(uint32_t)-1);
if(prim_group_rid==(uint32_t)-1){
/*thememberhasn'ttobeauseraccount->
*thereforenocheckneededinthiscase.*/
continue;
}

sid=dom_sid_add_rid(ac,samdb_domain_sid(ldb),
prim_group_rid);
if(sid==NULL){
returnldb_operr(ldb);
}

if(dom_sid_equal(group_sid,sid)){
ldb_asprintf_errstring(ldb,
"samldb:member%salreadysetviaprimaryGroupID%u",
ldb_dn_get_linearized(member_dn),prim_group_rid);
returnLDB_ERR_ENTRY_ALREADY_EXISTS;
}
}
}

talloc_free(res);

returnLDB_SUCCESS;
}

