/*add*/
staticintsamldb_add(structldb_module*module,structldb_request*req)
{
structldb_context*ldb;
structsamldb_ctx*ac;
structldb_message_element*el;
intret;

ldb=ldb_module_get_ctx(module);
ldb_debug(ldb,LDB_DEBUG_TRACE,"samldb_add\n");

/*donotmanipulateourcontrolentries*/
if(ldb_dn_is_special(req->op.add.message->dn)){
returnldb_next_request(module,req);
}

el=ldb_msg_find_element(req->op.add.message,"userParameters");
if(el!=NULL&&ldb_req_is_untrusted(req)){
constchar*reason="samldb_add:"
"settinguserParametersisnotsupportedoverLDAP,"
"seehttps://bugzilla.samba.org/show_bug.cgi?id=8077";
ldb_debug(ldb,LDB_DEBUG_WARNING,"%s",reason);
returnldb_error(ldb,LDB_ERR_CONSTRAINT_VIOLATION,reason);
}

ac=samldb_ctx_init(module,req);
if(ac==NULL){
returnldb_operr(ldb);
}

/*buildthenewmsg*/
ac->msg=ldb_msg_copy_shallow(ac,req->op.add.message);
if(ac->msg==NULL){
talloc_free(ac);
ldb_debug(ldb,LDB_DEBUG_FATAL,
"samldb_add:ldb_msg_copy_shallowfailed!\n");
returnldb_operr(ldb);
}

el=ldb_msg_find_element(ac->msg,"fSMORoleOwner");
if(el!=NULL){
ret=samldb_fsmo_role_owner_check(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

if(samdb_find_attribute(ldb,ac->msg,
"objectclass","user")!=NULL){
ac->type=SAMLDB_TYPE_USER;

ret=samldb_prim_group_trigger(ac);
if(ret!=LDB_SUCCESS){
returnret;
}

ret=samldb_objectclass_trigger(ac);
if(ret!=LDB_SUCCESS){
returnret;
}

returnsamldb_fill_object(ac);
}

if(samdb_find_attribute(ldb,ac->msg,
"objectclass","group")!=NULL){
ac->type=SAMLDB_TYPE_GROUP;

ret=samldb_objectclass_trigger(ac);
if(ret!=LDB_SUCCESS){
returnret;
}

returnsamldb_fill_object(ac);
}

/*perhapsaforeignSecurityPrincipal?*/
if(samdb_find_attribute(ldb,ac->msg,
"objectclass",
"foreignSecurityPrincipal")!=NULL){
returnsamldb_fill_foreignSecurityPrincipal_object(ac);
}

if(samdb_find_attribute(ldb,ac->msg,
"objectclass","classSchema")!=NULL){
ret=samldb_schema_info_update(ac);
if(ret!=LDB_SUCCESS){
talloc_free(ac);
returnret;
}

ac->type=SAMLDB_TYPE_CLASS;
returnsamldb_fill_object(ac);
}

if(samdb_find_attribute(ldb,ac->msg,
"objectclass","attributeSchema")!=NULL){
ret=samldb_schema_info_update(ac);
if(ret!=LDB_SUCCESS){
talloc_free(ac);
returnret;
}

ac->type=SAMLDB_TYPE_ATTRIBUTE;
returnsamldb_fill_object(ac);
}

talloc_free(ac);

/*nothingmatched,goon*/
returnldb_next_request(module,req);
}

/*modify*/
staticintsamldb_modify(structldb_module*module,structldb_request*req)
{
structldb_context*ldb;
structsamldb_ctx*ac;
structldb_message_element*el,*el2;
structldb_control*is_undelete;
boolmodified=false;
intret;

if(ldb_dn_is_special(req->op.mod.message->dn)){
/*donotmanipulateourcontrolentries*/
returnldb_next_request(module,req);
}

ldb=ldb_module_get_ctx(module);

/*
*wearegoingtoneedsomespecialhandlingifinUndeletecall.
*Sincetombstone_reanimatemodulewillrestorecertainattributes,
*weneedtorelaxchecksfor:sAMAccountType,primaryGroupID
*/
is_undelete=ldb_request_get_control(req,DSDB_CONTROL_RESTORE_TOMBSTONE_OID);

/*makesurethat"objectSid"isnotspecified*/
el=ldb_msg_find_element(req->op.mod.message,"objectSid");
if(el!=NULL){
if(ldb_request_get_control(req,LDB_CONTROL_PROVISION_OID)==NULL){
ldb_set_errstring(ldb,
"samldb:objectSidmustnotbespecified!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}
if(is_undelete==NULL){
/*makesurethat"sAMAccountType"isnotspecified*/
el=ldb_msg_find_element(req->op.mod.message,"sAMAccountType");
if(el!=NULL){
ldb_set_errstring(ldb,
"samldb:sAMAccountTypemustnotbespecified!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}
/*makesurethat"isCriticalSystemObject"isnotspecified*/
el=ldb_msg_find_element(req->op.mod.message,"isCriticalSystemObject");
if(el!=NULL){
if(ldb_request_get_control(req,LDB_CONTROL_RELAX_OID)==NULL){
ldb_set_errstring(ldb,
"samldb:isCriticalSystemObjectmustnotbespecified!");
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}

/*msDS-IntIdisnotallowedtobemodified
*exceptwhenmodificationcomesfromreplication*/
if(ldb_msg_find_element(req->op.mod.message,"msDS-IntId")){
if(!ldb_request_get_control(req,
DSDB_CONTROL_REPLICATED_UPDATE_OID)){
returnLDB_ERR_CONSTRAINT_VIOLATION;
}
}

el=ldb_msg_find_element(req->op.mod.message,"userParameters");
if(el!=NULL&&ldb_req_is_untrusted(req)){
constchar*reason="samldb:"
"settinguserParametersisnotsupportedoverLDAP,"
"seehttps://bugzilla.samba.org/show_bug.cgi?id=8077";
ldb_debug(ldb,LDB_DEBUG_WARNING,"%s",reason);
returnldb_error(ldb,LDB_ERR_CONSTRAINT_VIOLATION,reason);
}

ac=samldb_ctx_init(module,req);
if(ac==NULL){
returnldb_operr(ldb);
}

/*buildthenewmsg*/
ac->msg=ldb_msg_copy_shallow(ac,req->op.mod.message);
if(ac->msg==NULL){
talloc_free(ac);
ldb_debug(ldb,LDB_DEBUG_FATAL,
"samldb_modify:ldb_msg_copy_shallowfailed!\n");
returnldb_operr(ldb);
}

if(is_undelete==NULL){
el=ldb_msg_find_element(ac->msg,"primaryGroupID");
if(el!=NULL){
ret=samldb_prim_group_trigger(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}
}

el=ldb_msg_find_element(ac->msg,"userAccountControl");
if(el!=NULL){
modified=true;
ret=samldb_user_account_control_change(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"lockoutTime");
if(el!=NULL){
modified=true;
ret=samldb_lockout_time(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"groupType");
if(el!=NULL){
modified=true;
ret=samldb_group_type_change(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"sAMAccountName");
if(el!=NULL){
ret=samldb_sam_accountname_check(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"member");
if(el!=NULL){
ret=samldb_member_check(ac);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"description");
if(el!=NULL){
ret=samldb_description_check(ac,&modified);
if(ret!=LDB_SUCCESS){
returnret;
}
}

el=ldb_msg_find_element(ac->msg,"dNSHostName");
el2=ldb_msg_find_element(ac->msg,"sAMAccountName");
if((el!=NULL)||(el2!=NULL)){
modified=true;
ret=samldb_service_principal_names_change(ac);
if(ret!=LDB_SUCCESS){
returnret;
