/*rename*/

staticintcheck_rename_constraints(structldb_message*msg,
structsamldb_ctx*ac,
structldb_dn*olddn,structldb_dn*newdn)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
structldb_dn*dn1,*dn2,*nc_root;
int32_tsystemFlags;
boolmove_op=false;
boolrename_op=false;
intret;

/*SkipthechecksifoldandnewDNarethesame,orifwehavethe
*relaxcontrolspecifiedorifthereturnedobjectsisalready
*deletedandneedsonlytobemovedforconsistency.*/

if(ldb_dn_compare(olddn,newdn)==0){
returnLDB_SUCCESS;
}
if(ldb_request_get_control(ac->req,LDB_CONTROL_RELAX_OID)!=NULL){
returnLDB_SUCCESS;
}

if(ldb_msg_find_attr_as_bool(msg,"isDeleted",false)){
/*
*checkoriginatingrequestifwearesupposed
*to"see"thisrecordinfirstplace.
*/
if(ldb_request_get_control(ac->req,LDB_CONTROL_SHOW_DELETED_OID)==NULL){
returnLDB_ERR_NO_SUCH_OBJECT;
}
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*ObjectsunderCN=System*/

dn1=ldb_dn_copy(ac,ldb_get_default_basedn(ldb));
if(dn1==NULL)returnldb_oom(ldb);

if(!ldb_dn_add_child_fmt(dn1,"CN=System")){
talloc_free(dn1);
returnLDB_ERR_OPERATIONS_ERROR;
}

if((ldb_dn_compare_base(dn1,olddn)==0)&&
(ldb_dn_compare_base(dn1,newdn)!=0)){
talloc_free(dn1);
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotmove/rename%s.ObjectsunderCN=Systemhavetostayunderit!",
ldb_dn_get_linearized(olddn));
returnLDB_ERR_OTHER;
}

talloc_free(dn1);

/*LSAobjects*/

if((samdb_find_attribute(ldb,msg,"objectClass","secret")!=NULL)||
(samdb_find_attribute(ldb,msg,"objectClass","trustedDomain")!=NULL)){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotmove/rename%s.It'sanLSA-specificobject!",
ldb_dn_get_linearized(olddn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*systemFlags*/

dn1=ldb_dn_get_parent(ac,olddn);
if(dn1==NULL)returnldb_oom(ldb);
dn2=ldb_dn_get_parent(ac,newdn);
if(dn2==NULL)returnldb_oom(ldb);

if(ldb_dn_compare(dn1,dn2)==0){
rename_op=true;
}else{
move_op=true;
}

talloc_free(dn1);
talloc_free(dn2);

systemFlags=ldb_msg_find_attr_as_int(msg,"systemFlags",0);

/*Fetchnamecontext*/

ret=dsdb_find_nc_root(ldb,ac,olddn,&nc_root);
if(ret!=LDB_SUCCESS){
returnret;
}

if(ldb_dn_compare(nc_root,ldb_get_schema_basedn(ldb))==0){
if(move_op){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotmove%swithinschemapartition",
ldb_dn_get_linearized(olddn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
if(rename_op&&
(systemFlags&SYSTEM_FLAG_SCHEMA_BASE_OBJECT)!=0){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotrename%swithinschemapartition",
ldb_dn_get_linearized(olddn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}elseif(ldb_dn_compare(nc_root,ldb_get_config_basedn(ldb))==0){
if(move_op&&
(systemFlags&SYSTEM_FLAG_CONFIG_ALLOW_MOVE)==0){
/*Herewehavetodomore:controlthe
*"ALLOW_LIMITED_MOVE"flag.Thismeansthatthe
*grand-grand-parentsoftwoobjectshavetobeequal
*inordertoperformthemove(thisisusedfor
*moving"server"objectsinthe"sites"container).*/
boollimited_move=
systemFlags&SYSTEM_FLAG_CONFIG_ALLOW_LIMITED_MOVE;

if(limited_move){
dn1=ldb_dn_copy(ac,olddn);
if(dn1==NULL)returnldb_oom(ldb);
dn2=ldb_dn_copy(ac,newdn);
if(dn2==NULL)returnldb_oom(ldb);

limited_move&=ldb_dn_remove_child_components(dn1,3);
limited_move&=ldb_dn_remove_child_components(dn2,3);
limited_move&=ldb_dn_compare(dn1,dn2)==0;

talloc_free(dn1);
talloc_free(dn2);
}

if(!limited_move
&&ldb_request_get_control(ac->req,DSDB_CONTROL_RESTORE_TOMBSTONE_OID)==NULL){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotmove%sto%sinconfigpartition",
ldb_dn_get_linearized(olddn),ldb_dn_get_linearized(newdn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}
if(rename_op&&
(systemFlags&SYSTEM_FLAG_CONFIG_ALLOW_RENAME)==0){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotrename%sto%swithinconfigpartition",
ldb_dn_get_linearized(olddn),ldb_dn_get_linearized(newdn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}elseif(ldb_dn_compare(nc_root,ldb_get_default_basedn(ldb))==0){
if(move_op&&
(systemFlags&SYSTEM_FLAG_DOMAIN_DISALLOW_MOVE)!=0){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotmove%sto%s-DISALLOW_MOVEset",
ldb_dn_get_linearized(olddn),ldb_dn_get_linearized(newdn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
if(rename_op&&
(systemFlags&SYSTEM_FLAG_DOMAIN_DISALLOW_RENAME)!=0){
ldb_asprintf_errstring(ldb,
"subtree_rename:Cannotrename%sto%s-DISALLOW_RENAMEset",
ldb_dn_get_linearized(olddn),ldb_dn_get_linearized(newdn));
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
}

talloc_free(nc_root);

returnLDB_SUCCESS;
}


staticintsamldb_rename_search_base_callback(structldb_request*req,
structldb_reply*ares)
{
structsamldb_ctx*ac;
intret;

ac=talloc_get_type(req->context,structsamldb_ctx);

if(!ares){
returnldb_module_done(ac->req,NULL,NULL,
LDB_ERR_OPERATIONS_ERROR);
}
if(ares->error!=LDB_SUCCESS){
returnldb_module_done(ac->req,ares->controls,
ares->response,ares->error);
}

switch(ares->type){
caseLDB_REPLY_ENTRY:
/*
*Thisistherootentryoftheoriginatingmove
*respectivelyrenamerequest.Ithasbeenalready
*storedinthelistusing"subtree_rename_search()".
*Onlythisoneissubjecttoconstraintchecking.
*/
ret=check_rename_constraints(ares->message,ac,
ac->req->op.rename.olddn,
ac->req->op.rename.newdn);
if(ret!=LDB_SUCCESS){
returnldb_module_done(ac->req,NULL,NULL,
ret);
}
break;

caseLDB_REPLY_REFERRAL:
/*ignore*/
break;

caseLDB_REPLY_DONE:

/*
*Great,noproblemwiththerename,sogoaheadas
*ifweneverwerehere
*/
ret=ldb_next_request(ac->module,ac->req);
talloc_free(ares);
returnret;
}

talloc_free(ares);
returnLDB_SUCCESS;
}


