msds_intid_struct=(structsamldb_msds_intid_persistant*)ldb_get_opaque(ldb,SAMLDB_MSDS_INTID_OPAQUE);
if(!msds_intid_struct){
msds_intid_struct=talloc(ldb,structsamldb_msds_intid_persistant);
/*GeneratenewvalueformsDs-IntId
*Valueshouldbein0x80000000..0xBFFFFFFFrange*/
msds_intid=generate_random()%0X3FFFFFFF;
msds_intid+=0x80000000;
