}

/*current_usncanbelesserthanmsds_intid_struct->ifthereis
*uncommitedchanges.
*/
if(current_usn>msds_intid_struct->usn){
/*oupssomethinghaschanged,someone/something
*elseismodifyingorhasmodifiedtheschema
*we'dbettercheckthisintidisthedatabasedirectly
*/

DEBUG(2,("Schemahaschanged,searchingthedatabasefortheunicityof%d\n",
msds_intid));

ret=dsdb_module_search(ac->module,ac,
&ldb_res,
schema_dn,LDB_SCOPE_ONELEVEL,NULL,
DSDB_FLAG_NEXT_MODULE,
ac->req,
"(msDS-IntId=%d)",msds_intid);
if(ret!=LDB_SUCCESS){
