staticintsamldb_prim_group_change(structsamldb_ctx*ac)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
constchar*constattrs[]={
"primaryGroupID",
"memberOf",
"userAccountControl",
NULL};
structldb_result*res,*group_res;
structldb_message_element*el;
structldb_message*msg;
uint32_tprev_rid,new_rid,uac;
structdom_sid*prev_sid,*new_sid;
structldb_dn*prev_prim_group_dn,*new_prim_group_dn;
intret;
constchar*constnoattrs[]={NULL};

el=dsdb_get_single_valued_attr(ac->msg,"primaryGroupID",
ac->req->operation);
if(el==NULL){
/*wearenotaffected*/
returnLDB_SUCCESS;
}

/*Fetchinformationfromtheexistingobject*/

ret=dsdb_module_search_dn(ac->module,ac,&res,ac->msg->dn,attrs,
DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}

uac=ldb_msg_find_attr_as_uint(res->msgs[0],"userAccountControl",0);

/*FindsouttheDNoftheoldprimarygroup*/

prev_rid=ldb_msg_find_attr_as_uint(res->msgs[0],"primaryGroupID",
(uint32_t)-1);
if(prev_rid==(uint32_t)-1){
/*Userobjectsdoalwayshaveamandatory"primaryGroupID"
*attribute.Ifthisdoesn'texistthentheobjectisofthe
*wrongtype.ThisistheexactWindowserrorcode*/
returnLDB_ERR_OBJECT_CLASS_VIOLATION;
}

prev_sid=dom_sid_add_rid(ac,samdb_domain_sid(ldb),prev_rid);
if(prev_sid==NULL){
returnldb_operr(ldb);
}

/*FindsouttheDNofthenewprimarygroup
*Notice:inordertoparsetheprimarygroupIDcorrectlywecreate
*atemporarymessagehere.*/

msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(msg,el,0);
if(ret!=LDB_SUCCESS){
returnret;
}
new_rid=ldb_msg_find_attr_as_uint(msg,"primaryGroupID",(uint32_t)-1);
talloc_free(msg);
if(new_rid==(uint32_t)-1){
/*wearen'taffectedofanyprimarygroupchange*/
returnLDB_SUCCESS;
}

if(prev_rid==new_rid){
returnLDB_SUCCESS;
}

if((uac&UF_SERVER_TRUST_ACCOUNT)&&new_rid!=DOMAIN_RID_DCS){
ldb_asprintf_errstring(ldb,
"%08X:samldb:UF_SERVER_TRUST_ACCOUNTrequires"
"primaryGroupID=%u!",
W_ERROR_V(WERR_DS_CANT_MOD_PRIMARYGROUPID),
DOMAIN_RID_DCS);
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

if((uac&UF_PARTIAL_SECRETS_ACCOUNT)&&new_rid!=DOMAIN_RID_READONLY_DCS){
ldb_asprintf_errstring(ldb,
"%08X:samldb:UF_PARTIAL_SECRETS_ACCOUNTrequires"
"primaryGroupID=%u!",
W_ERROR_V(WERR_DS_CANT_MOD_PRIMARYGROUPID),
DOMAIN_RID_READONLY_DCS);
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

ret=dsdb_module_search(ac->module,ac,&group_res,
ldb_get_default_basedn(ldb),
LDB_SCOPE_SUBTREE,
noattrs,DSDB_FLAG_NEXT_MODULE,
ac->req,
"(objectSid=%s)",
ldap_encode_ndr_dom_sid(ac,prev_sid));
if(ret!=LDB_SUCCESS){
returnret;
}
if(group_res->count!=1){
returnldb_operr(ldb);
}
prev_prim_group_dn=group_res->msgs[0]->dn;

new_sid=dom_sid_add_rid(ac,samdb_domain_sid(ldb),new_rid);
if(new_sid==NULL){
returnldb_operr(ldb);
}

ret=dsdb_module_search(ac->module,ac,&group_res,
ldb_get_default_basedn(ldb),
LDB_SCOPE_SUBTREE,
noattrs,DSDB_FLAG_NEXT_MODULE,
ac->req,
"(objectSid=%s)",
ldap_encode_ndr_dom_sid(ac,new_sid));
if(ret!=LDB_SUCCESS){
returnret;
}
if(group_res->count!=1){
/*Hereweknowifthespecifiednewprimarygroupcandidateis
*validornot.*/
returnLDB_ERR_UNWILLING_TO_PERFORM;
}
new_prim_group_dn=group_res->msgs[0]->dn;

/*Weneedtobealreadyanormalmemberofthenewprimary
*groupinordertobesuccessful.*/
el=samdb_find_attribute(ldb,res->msgs[0],"memberOf",
ldb_dn_get_linearized(new_prim_group_dn));
if(el==NULL){
returnLDB_ERR_UNWILLING_TO_PERFORM;
}

/*Removethe"member"attributeonthenewprimarygroup*/
msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
msg->dn=new_prim_group_dn;

ret=samdb_msg_add_delval(ldb,msg,msg,"member",
ldb_dn_get_linearized(ac->msg->dn));
if(ret!=LDB_SUCCESS){
returnret;
}

ret=dsdb_module_modify(ac->module,msg,DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}
talloc_free(msg);

/*Adda"member"attributeforthepreviousprimarygroup*/
msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
msg->dn=prev_prim_group_dn;

ret=samdb_msg_add_addval(ldb,msg,msg,"member",
ldb_dn_get_linearized(ac->msg->dn));
if(ret!=LDB_SUCCESS){
returnret;
}

ret=dsdb_module_modify(ac->module,msg,DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}
talloc_free(msg);

returnLDB_SUCCESS;
}

