.error_string="SettingUF_TRUSTED_FOR_DELEGATIONnotallowedwithUF_PARTIAL_SECRETS_ACCOUNT"
}
};

for(i=0;i<ARRAY_SIZE(map);i++){
if(user_account_control&map[i].uac){
need_check=true;
break;
}
}
if(need_check==false){
returnLDB_SUCCESS;
}

for(i=0;i<ARRAY_SIZE(map);i++){
uint32_tthis_uac=user_account_control&map[i].uac;
if(this_uac!=0){
if(map[i].never){
ret=LDB_ERR_OTHER;
break;
}elseif(map[i].needs!=0){
if((map[i].needs&user_account_control)==0){
ret=LDB_ERR_OTHER;
break;
}
}elseif(map[i].not_with!=0){
if((map[i].not_with&user_account_control)!=0){
ret=LDB_ERR_OTHER;
break;
}
}
}
}
if(ret!=LDB_SUCCESS){
switch(ac->req->operation){
caseLDB_ADD:
ldb_asprintf_errstring(ldb_module_get_ctx(ac->module),
"Failedtoadd%s:%s",
ldb_dn_get_linearized(ac->msg->dn),
map[i].error_string);
break;
