/*Thistriggeradaptsthe"servicePrincipalName"attributesifthe
*"dNSHostName"and/or"sAMAccountName"attributechange(s)*/
staticintsamldb_service_principal_names_change(structsamldb_ctx*ac)
{
structldb_context*ldb=ldb_module_get_ctx(ac->module);
structldb_message_element*el=NULL,*el2=NULL;
structldb_message*msg;
constchar*constattrs[]={"servicePrincipalName",NULL};
structldb_result*res;
constchar*dns_hostname=NULL,*old_dns_hostname=NULL,
*sam_accountname=NULL,*old_sam_accountname=NULL;
unsignedinti,j;
intret;

el=dsdb_get_single_valued_attr(ac->msg,"dNSHostName",
ac->req->operation);
el2=dsdb_get_single_valued_attr(ac->msg,"sAMAccountName",
ac->req->operation);
if((el==NULL)&&(el2==NULL)){
/*wearenotaffected*/
returnLDB_SUCCESS;
}

/*Createatemporarymessageforfetchingthe"dNSHostName"*/
if(el!=NULL){
constchar*dns_attrs[]={"dNSHostName",NULL};
msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(msg,el,0);
if(ret!=LDB_SUCCESS){
returnret;
}
dns_hostname=talloc_strdup(ac,
ldb_msg_find_attr_as_string(msg,"dNSHostName",NULL));
if(dns_hostname==NULL){
returnldb_module_oom(ac->module);
}

talloc_free(msg);

ret=dsdb_module_search_dn(ac->module,ac,&res,ac->msg->dn,
dns_attrs,DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret==LDB_SUCCESS){
old_dns_hostname=ldb_msg_find_attr_as_string(res->msgs[0],"dNSHostName",NULL);
}
}

/*Createatemporarymessageforfetchingthe"sAMAccountName"*/
if(el2!=NULL){
char*tempstr,*tempstr2=NULL;
constchar*acct_attrs[]={"sAMAccountName",NULL};

msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
ret=ldb_msg_add(msg,el2,0);
if(ret!=LDB_SUCCESS){
returnret;
}
tempstr=talloc_strdup(ac,
ldb_msg_find_attr_as_string(msg,"sAMAccountName",NULL));
talloc_free(msg);

ret=dsdb_module_search_dn(ac->module,ac,&res,ac->msg->dn,acct_attrs,
DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret==LDB_SUCCESS){
tempstr2=talloc_strdup(ac,
ldb_msg_find_attr_as_string(res->msgs[0],
"sAMAccountName",NULL));
}


/*The"sAMAccountName"needssomeadditionaltrimming:weneed
*toremovethetrailing"$"siftheyexist.*/
if((tempstr!=NULL)&&(tempstr[0]!='\0')&&
(tempstr[strlen(tempstr)-1]=='$')){
tempstr[strlen(tempstr)-1]='\0';
}
if((tempstr2!=NULL)&&(tempstr2[0]!='\0')&&
(tempstr2[strlen(tempstr2)-1]=='$')){
tempstr2[strlen(tempstr2)-1]='\0';
}
sam_accountname=tempstr;
old_sam_accountname=tempstr2;
}

if(old_dns_hostname==NULL){
/*wecannotchangewhentheoldnameisunknown*/
dns_hostname=NULL;
}
if((old_dns_hostname!=NULL)&&(dns_hostname!=NULL)&&
(strcasecmp_m(old_dns_hostname,dns_hostname)==0)){
/*The"dNSHostName"didn'tchange*/
dns_hostname=NULL;
}

if(old_sam_accountname==NULL){
/*wecannotchangewhentheoldnameisunknown*/
sam_accountname=NULL;
}
if((old_sam_accountname!=NULL)&&(sam_accountname!=NULL)&&
(strcasecmp_m(old_sam_accountname,sam_accountname)==0)){
/*The"sAMAccountName"didn'tchange*/
sam_accountname=NULL;
}

if((dns_hostname==NULL)&&(sam_accountname==NULL)){
/*Well,thereareinformationmissing(oldname(s))orthe
*namesdidn'tchange.We'venothingtodoandcanexithere*/
returnLDB_SUCCESS;
}

/*Potential"servicePrincipalName"changesinthesamerequesthaveto
*behandledbeforetheupdate(Windowsbehaviour).*/
el=ldb_msg_find_element(ac->msg,"servicePrincipalName");
if(el!=NULL){
msg=ldb_msg_new(ac->msg);
if(msg==NULL){
returnldb_module_oom(ac->module);
}
msg->dn=ac->msg->dn;

do{
ret=ldb_msg_add(msg,el,el->flags);
if(ret!=LDB_SUCCESS){
returnret;
}

ldb_msg_remove_element(ac->msg,el);

el=ldb_msg_find_element(ac->msg,
"servicePrincipalName");
}while(el!=NULL);

ret=dsdb_module_modify(ac->module,msg,
DSDB_FLAG_NEXT_MODULE,ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}
talloc_free(msg);
}

/*Fetchthe"servicePrincipalName"sifany*/
ret=dsdb_module_search(ac->module,ac,&res,ac->msg->dn,LDB_SCOPE_BASE,attrs,
DSDB_FLAG_NEXT_MODULE,ac->req,NULL);
if(ret!=LDB_SUCCESS){
returnret;
}
if((res->count!=1)||(res->msgs[0]->num_elements>1)){
returnldb_operr(ldb);
}

if(res->msgs[0]->num_elements==1){
/*
*Yes,wedohave"servicePrincipalName"s.Firstweupdatethem
*locally,thatmeanswedoalwayssubstitutethecurrent
*"dNSHostName"withthenewoneand/or"sAMAccountName"
*without"$"withthenewoneandthenweappendthe
*modified"servicePrincipalName"sasamessageelement
*replacetothemodificationrequest(Windowsbehaviour).We
*needalsotomakesurethatthevaluesremaincase-
*insensitivelyunique.
*/

ret=ldb_msg_add_empty(ac->msg,"servicePrincipalName",
LDB_FLAG_MOD_REPLACE,&el);
if(ret!=LDB_SUCCESS){
returnret;
}

for(i=0;i<res->msgs[0]->elements[0].num_values;i++){
char*old_str,*new_str;
char*pos=NULL;
constchar*tok;
structldb_val*vals;
boolfound=false;

old_str=(char*)
res->msgs[0]->elements[0].values[i].data;

new_str=talloc_strdup(ac->msg,
strtok_r(old_str,"/",&pos));
if(new_str==NULL){
returnldb_module_oom(ac->module);
}

while((tok=strtok_r(NULL,"/",&pos))!=NULL){
if((dns_hostname!=NULL)&&
(strcasecmp_m(tok,old_dns_hostname)==0)){
tok=dns_hostname;
}
if((sam_accountname!=NULL)&&
(strcasecmp_m(tok,old_sam_accountname)==0)){
tok=sam_accountname;
}

new_str=talloc_asprintf(ac->msg,"%s/%s",
new_str,tok);
if(new_str==NULL){
returnldb_module_oom(ac->module);
}
}

/*Uniquenesscheck*/
for(j=0;(!found)&&(j<el->num_values);j++){
if(strcasecmp_m((char*)el->values[j].data,
new_str)==0){
found=true;
}
}
if(found){
continue;
}

/*
*appendthenew"servicePrincipalName"-
*codederivedfromldb_msg_add_value().
*
*Opencodedtomakeitclearthatwemust
*appendtotheMOD_REPLACEelcreatedabove.
*/
vals=talloc_realloc(ac->msg,el->values,
structldb_val,
el->num_values+1);
if(vals==NULL){
returnldb_module_oom(ac->module);
}
el->values=vals;
el->values[el->num_values]=data_blob_string_const(new_str);
++(el->num_values);
}
}

talloc_free(res);

returnLDB_SUCCESS;
}

