/*delete*/

staticintsamldb_prim_group_users_check(structsamldb_ctx*ac)
{
structldb_context*ldb;
structdom_sid*sid;
uint32_trid;
NTSTATUSstatus;
intret;
structldb_result*res;
constchar*constattrs[]={"objectSid","isDeleted",NULL};
constchar*constnoattrs[]={NULL};

ldb=ldb_module_get_ctx(ac->module);

/*FindsouttheSID/RIDoftheSAMobject*/
ret=dsdb_module_search_dn(ac->module,ac,&res,ac->req->op.del.dn,
attrs,
DSDB_FLAG_NEXT_MODULE|DSDB_SEARCH_SHOW_DELETED,
ac->req);
if(ret!=LDB_SUCCESS){
returnret;
}

if(ldb_msg_check_string_attribute(res->msgs[0],"isDeleted","TRUE")){
returnLDB_SUCCESS;
}

sid=samdb_result_dom_sid(ac,res->msgs[0],"objectSid");
if(sid==NULL){
/*NoSID-itmightnotbeaSAMobject-thereforeok*/
returnLDB_SUCCESS;
}
status=dom_sid_split_rid(ac,sid,NULL,&rid);
if(!NT_STATUS_IS_OK(status)){
returnldb_operr(ldb);
}
if(rid==0){
/*Specialobject(securityprincipal?)*/
returnLDB_SUCCESS;
}
/*donotallowdeletionofwell-knownsids*/
if(rid<DSDB_SAMDB_MINIMUM_ALLOWED_RID&&
(ldb_request_get_control(ac->req,LDB_CONTROL_RELAX_OID)==NULL)){
returnLDB_ERR_OTHER;
}

/*Denydeleterequestsfromgroupswhichareprimaryones*/
ret=dsdb_module_search(ac->module,ac,&res,
ldb_get_default_basedn(ldb),
LDB_SCOPE_SUBTREE,noattrs,
DSDB_FLAG_NEXT_MODULE,
ac->req,
"(&(primaryGroupID=%u)(objectClass=user))",rid);
if(ret!=LDB_SUCCESS){
returnret;
}
if(res->count>0){
returnLDB_ERR_ENTRY_ALREADY_EXISTS;
}

returnLDB_SUCCESS;
}

