/*{{{preg_get_backref
*/
staticintpreg_get_backref(char**str,int*backref)
{
registercharin_brace=0;
registerchar*walk=*str;

if(walk[1]==0)
return0;

if(*walk=='$'&&walk[1]=='{'){
in_brace=1;
walk++;
}
walk++;

if(*walk>='0'&&*walk<='9'){
*backref=*walk-'0';
walk++;
}else
return0;

if(*walk&&*walk>='0'&&*walk<='9'){
*backref=*backref*10+*walk-'0';
walk++;
}

if(in_brace){
if(*walk==0||*walk!='}')
return0;
else
walk++;
}

*str=walk;
return1;
}
