/*{{{pcre_get_compiled_regex_cache
*/
PHPAPIpcre_cache_entry*pcre_get_compiled_regex_cache(char*regex,intregex_lenTSRMLS_DC)
{
pcre*re=NULL;
pcre_extra*extra;
intcoptions=0;
intsoptions=0;
constchar*error;
interroffset;
chardelimiter;
charstart_delimiter;
charend_delimiter;
char*p,*pp;
char*pattern;
intdo_study=0;
intpoptions=0;
intcount=0;
unsignedconstchar*tables=NULL;
#ifHAVE_SETLOCALE
char*locale;
#endif
pcre_cache_entry*pce;
pcre_cache_entrynew_entry;
char*tmp=NULL;

#ifHAVE_SETLOCALE
#ifdefined(PHP_WIN32)&&defined(ZTS)
_configthreadlocale(_ENABLE_PER_THREAD_LOCALE);
#endif
locale=setlocale(LC_CTYPE,NULL);
#endif

/*Trytolookupthecachedregexentry,andifsuccessful,justpass
backthecompiledpattern,otherwisegoonandcompileit.*/
if(zend_hash_find(&PCRE_G(pcre_cache),regex,regex_len+1,(void**)&pce)==SUCCESS){
/*
*Weuseaquickpcre_fullinfo()checktoseewhethercacheiscorrupted,andifit
*is,weflushitandcompilethepatternfromscratch.
*/
if(pcre_fullinfo(pce->re,NULL,PCRE_INFO_CAPTURECOUNT,&count)==PCRE_ERROR_BADMAGIC){
zend_hash_clean(&PCRE_G(pcre_cache));
}else{
#ifHAVE_SETLOCALE
if(!strcmp(pce->locale,locale)){
#endif
returnpce;
#ifHAVE_SETLOCALE
}
#endif
}
}

p=regex;

/*Parsethroughtheleadingwhitespace,anddisplayawarningifwe
gettotheendwithoutencounteringadelimiter.*/
while(isspace((int)*(unsignedchar*)p))p++;
if(*p==0){
php_error_docref(NULLTSRMLS_CC,E_WARNING,
p<regex+regex_len?"Nullbyteinregex":"Emptyregularexpression");
returnNULL;
}

/*Getthedelimiteranddisplayawarningifitisalphanumeric
orabackslash.*/
delimiter=*p++;
if(isalnum((int)*(unsignedchar*)&delimiter)||delimiter=='\\'){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Delimitermustnotbealphanumericorbackslash");
returnNULL;
}

start_delimiter=delimiter;
if((pp=strchr("([{<)]}>)]}>",delimiter)))
delimiter=pp[5];
end_delimiter=delimiter;

pp=p;

if(start_delimiter==end_delimiter){
/*Weneedtoiteratethroughthepattern,searchingfortheendingdelimiter,
butskippingthebackslasheddelimiters.Iftheendingdelimiterisnot
found,displayawarning.*/
while(*pp!=0){
if(*pp=='\\'&&pp[1]!=0)pp++;
elseif(*pp==delimiter)
break;
pp++;
}
}else{
/*Weiteratethroughthepattern,searchingforthematchingending
*delimiter.Foreachmatchingstartingdelimiter,weincrementnesting
*level,anddecrementitforeachmatchingendingdelimiter.Ifwe
*reachtheendofthepatternwithoutmatching,displayawarning.
*/
intbrackets=1;/*bracketsnestinglevel*/
while(*pp!=0){
if(*pp=='\\'&&pp[1]!=0)pp++;
elseif(*pp==end_delimiter&&--brackets<=0)
break;
elseif(*pp==start_delimiter)
brackets++;
pp++;
}
}

if(*pp==0){
if(pp<regex+regex_len){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Nullbyteinregex");
}elseif(start_delimiter==end_delimiter){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Noendingdelimiter'%c'found",delimiter);
}else{
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Noendingmatchingdelimiter'%c'found",delimiter);
}
returnNULL;
}

/*Makeacopyoftheactualpattern.*/
pattern=estrndup(p,pp-p);

/*Moveontotheoptions*/
pp++;

/*Parsethroughtheoptions,settingappropriateflags.Display
awarningifweencounteranunknownmodifier.*/
while(pp<regex+regex_len){
switch(*pp++){
/*Perlcompatibleoptions*/
case'i':coptions|=PCRE_CASELESS;break;
case'm':coptions|=PCRE_MULTILINE;break;
case's':coptions|=PCRE_DOTALL;break;
case'x':coptions|=PCRE_EXTENDED;break;

/*PCREspecificoptions*/
case'A':coptions|=PCRE_ANCHORED;break;
case'D':coptions|=PCRE_DOLLAR_ENDONLY;break;
case'S':do_study=1;break;
case'U':coptions|=PCRE_UNGREEDY;break;
case'X':coptions|=PCRE_EXTRA;break;
case'u':coptions|=PCRE_UTF8;
/*InPCRE,bydefault,\d,\D,\s,\S,\w,and\WrecognizeonlyASCII
characters,eveninUTF-8mode.However,thiscanbechangedbysetting
thePCRE_UCPoption.*/
#ifdefPCRE_UCP
coptions|=PCRE_UCP;
#endif
break;

/*Custompregoptions*/
case'e':poptions|=PREG_REPLACE_EVAL;break;

case'':
case'\n':
break;

default:
if(pp[-1]){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Unknownmodifier'%c'",pp[-1]);
}else{
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Nullbyteinregex");
}
efree(pattern);
returnNULL;
}
}

#ifHAVE_SETLOCALE
if(strcmp(locale,"C"))
tables=pcre_maketables();
#endif

/*Compilepatternanddisplayawarningifcompilationfailed.*/
re=pcre_compile(pattern,
coptions,
&error,
&erroffset,
tables);

if(re==NULL){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Compilationfailed:%satoffset%d",error,erroffset);
efree(pattern);
if(tables){
pefree((void*)tables,1);
}
returnNULL;
}

/*Ifstudyoptionwasspecified,studythepatternand
storetheresultinextraforpassingtopcre_exec.*/
if(do_study){
extra=pcre_study(re,soptions,&error);
if(extra){
extra->flags|=PCRE_EXTRA_MATCH_LIMIT|PCRE_EXTRA_MATCH_LIMIT_RECURSION;
}
if(error!=NULL){
php_error_docref(NULLTSRMLS_CC,E_WARNING,"Errorwhilestudyingpattern");
}
}else{
extra=NULL;
}

efree(pattern);

/*
*Ifwereachedcachelimit,cleanouttheitemsfromtheheadofthelist;
*thesearesupposedlytheoldestones(butnotnecessarilytheleastused
*ones).
*/
if(zend_hash_num_elements(&PCRE_G(pcre_cache))==PCRE_CACHE_SIZE){
intnum_clean=PCRE_CACHE_SIZE/8;
zend_hash_apply_with_argument(&PCRE_G(pcre_cache),pcre_clean_cache,&num_cleanTSRMLS_CC);
}

/*Storethecompiledpatternandextrainfointhecache.*/
new_entry.re=re;
new_entry.extra=extra;
new_entry.preg_options=poptions;
new_entry.compile_options=coptions;
#ifHAVE_SETLOCALE
new_entry.locale=pestrdup(locale,1);
new_entry.tables=tables;
#endif

/*
*InternedstringsarenotduplicatedwhenstoredinHashTable,
*butalltheinternedstringscreatedduringHTTPrequestareremoved
*atendofrequest.HoweverPCRE_G(pcre_cache)mustbeconsistent
*onthenextrequestaswell.Sowedisableusageofinternedstrings
*ashashkeysespecuallyforthistable.
*Seebug#63180
*/
if(IS_INTERNED(regex)){
regex=tmp=estrndup(regex,regex_len);
}

zend_hash_update(&PCRE_G(pcre_cache),regex,regex_len+1,(void*)&new_entry,
sizeof(pcre_cache_entry),(void**)&pce);

if(tmp){
efree(tmp);
}

returnpce;
}
/*}}}*/

