int*offsets;/*Arrayofsubpatternoffsets*/
intnum_subpats;/*Numberofcapturedsubpatterns*/
intsize_offsets;/*Sizeoftheoffsetsarray*/
intmatched;/*Hasanythingmatched*/
intg_notempty=0;/*Ifthematchshouldnotbeempty*/
