add_offset_pair(subpats,(char*)stringlist[i],
offsets[(i<<1)+1]-offsets[i<<1],
offsets[i<<1],subpat_names[i]);
}else{
if(subpat_names[i]){
add_assoc_stringl(subpats,subpat_names[i],(char*)stringlist[i],
offsets[(i<<1)+1]-offsets[i<<1],1);
